package com.wipro.flowcontrolstatements;

public class Charactersinalphabeticalorder {

	public static void main(String[] args) 
	{
		char x = 'a';
		char y = 'e';
		if (x<y) {
			System.out.println(x+","+y);
		}
		else
		{
			System.out.println(y+","+x);
		}
		// TODO Auto-generated method stub

	}

}
	