package com.wipro.flowcontrolstatements;

public class Oddoreven 
{

	public static void main(String[] args) 
	{
		int a = Integer.parseInt(args[0]);
		if (a%2==0)
		{
			System.out.printf("Even");
		}
		else
		{
			System.out.printf("Odd");
		}
		// TODO Auto-generated method stub

	}

}
